import React, { useState, useEffect } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

const TaskSchema = Yup.object().shape({
  title: Yup.string()
    .min(2, 'Title must be at least 2 characters')
    .max(200, 'Title must be less than 200 characters')
    .required('Title is required'),
  description: Yup.string()
    .min(10, 'Description must be at least 10 characters')
    .required('Description is required'),
  status: Yup.string()
    .oneOf(['pending', 'in_progress', 'completed', 'blocked'], 'Invalid status')
    .required('Status is required'),
  priority: Yup.string()
    .oneOf(['low', 'medium', 'high', 'critical'], 'Invalid priority')
    .required('Priority is required'),
  due_date: Yup.date()
    .min(new Date(), 'Due date must be in the future')
    .required('Due date is required'),
  project_id: Yup.number()
    .required('Project is required'),
  assignee_id: Yup.number()
    .nullable()
});

const TaskForm = ({ task, projects, users, onSubmit, onSuccess, onCancel }) => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (values, { resetForm }) => {
    setIsSubmitting(true);
    setError('');

    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'Content-Type': 'application/json',
          'X-User-ID': localStorage.getItem('userId'),
          'Authorization': token ? `Bearer ${token}` : ''
        }
      };

      let response;
      if (task) {
        // Update existing task
        response = await axios.put(
          `http://localhost:5000/api/tasks/${task.id}`, 
          values,
          config
        );
      } else {
        // Create new task
        response = await axios.post(
          'http://localhost:5000/api/tasks', 
          values,
          config
        );
      }

      if (onSubmit) {
        onSubmit(response.data);
      }

      if (onSuccess) {
        onSuccess(response.data);
      }

      resetForm();
    } catch (err) {
      console.error('Error saving task:', err);
      setError(err.response?.data?.error || 'Failed to save task. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const initialValues = task ? {
    title: task.title || '',
    description: task.description || '',
    status: task.status || 'pending',
    priority: task.priority || 'medium',
    due_date: task.due_date ? new Date(task.due_date).toISOString().split('T')[0] : '',
    project_id: task.project_id || '',
    assignee_id: task.assignee_id || ''
  } : {
    title: '',
    description: '',
    status: 'pending',
    priority: 'medium',
    due_date: '',
    project_id: '',
    assignee_id: ''
  };

  return (
    <div className="task-form-container">
      <h3>{task ? 'Edit Task' : 'Create New Task'}</h3>

      {error && <div className="alert alert-danger">{error}</div>}

      <Formik
        initialValues={initialValues}
        validationSchema={TaskSchema}
        onSubmit={handleSubmit}
        enableReinitialize
      >
        {({ errors, touched, values }) => (
          <Form className="task-form">
            <div className="form-group">
              <label htmlFor="title">Title*</label>
              <Field 
                type="text" 
                name="title" 
                className={`form-control ${errors.title && touched.title ? 'is-invalid' : ''}`}
              />
              <ErrorMessage name="title" component="div" className="invalid-feedback" />
            </div>

            <div className="form-group">
              <label htmlFor="description">Description*</label>
              <Field 
                as="textarea" 
                name="description" 
                rows="3"
                className={`form-control ${errors.description && touched.description ? 'is-invalid' : ''}`}
              />
              <ErrorMessage name="description" component="div" className="invalid-feedback" />
            </div>

            <div className="row">
              <div className="col-md-6">
                <div className="form-group">
                  <label htmlFor="status">Status*</label>
                  <Field 
                    as="select" 
                    name="status" 
                    className={`form-control ${errors.status && touched.status ? 'is-invalid' : ''}`}
                  >
                    <option value="pending">Pending</option>
                    <option value="in_progress">In Progress</option>
                    <option value="completed">Completed</option>
                    <option value="blocked">Blocked</option>
                  </Field>
                  <ErrorMessage name="status" component="div" className="invalid-feedback" />
                </div>
              </div>

              <div className="col-md-6">
                <div className="form-group">
                  <label htmlFor="priority">Priority*</label>
                  <Field 
                    as="select" 
                    name="priority" 
                    className={`form-control ${errors.priority && touched.priority ? 'is-invalid' : ''}`}
                  >
                    <option value="low">Low</option>
                    <option value="medium">Medium</option>
                    <option value="high">High</option>
                    <option value="critical">Critical</option>
                  </Field>
                  <ErrorMessage name="priority" component="div" className="invalid-feedback" />
                </div>
              </div>
            </div>

            <div className="row">
              <div className="col-md-6">
                <div className="form-group">
                  <label htmlFor="due_date">Due Date*</label>
                  <Field 
                    type="date" 
                    name="due_date" 
                    className={`form-control ${errors.due_date && touched.due_date ? 'is-invalid' : ''}`}
                  />
                  <ErrorMessage name="due_date" component="div" className="invalid-feedback" />
                </div>
              </div>

              <div className="col-md-6">
                <div className="form-group">
                  <label htmlFor="project_id">Project*</label>
                  <Field 
                    as="select" 
                    name="project_id" 
                    className={`form-control ${errors.project_id && touched.project_id ? 'is-invalid' : ''}`}
                  >
                    <option value="">Select a project</option>
                    {projects.map(project => (
                      <option key={project.id} value={project.id}>
                        {project.name}
                      </option>
                    ))}
                  </Field>
                  <ErrorMessage name="project_id" component="div" className="invalid-feedback" />
                </div>
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="assignee_id">Assignee</label>
              <Field 
                as="select" 
                name="assignee_id" 
                className="form-control"
              >
                <option value="">Unassigned</option>
                {users.map(user => (
                  <option key={user.id} value={user.id}>
                    {user.name} ({user.role})
                  </option>
                ))}
              </Field>
            </div>

            <div className="form-actions">
              <button 
                type="submit" 
                className="btn btn-primary"
                disabled={isSubmitting}
              >
                {isSubmitting ? (
                  <>
                    <span className="spinner-border spinner-border-sm mr-2" role="status" aria-hidden="true"></span>
                    Saving...
                  </>
                ) : task ? 'Update Task' : 'Create Task'}
              </button>

              {onCancel && (
                <button 
                  type="button" 
                  className="btn btn-outline-secondary ml-2"
                  onClick={onCancel}
                  disabled={isSubmitting}
                >
                  Cancel
                </button>
              )}
            </div>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default TaskForm;
