import React, { useState } from 'react';
import axios from 'axios';
import { format } from 'date-fns';

const TaskCard = ({ task, users, projects, onUpdate, onDelete }) => {
  const [currentTask, setCurrentTask] = useState(task);
  const [isEditing, setIsEditing] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [error, setError] = useState('');

  const assignee = users.find(user => user.id === currentTask.assignee_id);
  const project = projects.find(proj => proj.id === currentTask.project_id);

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed': return '#2ecc71';
      case 'in_progress': return '#f39c12';
      case 'blocked': return '#e74c3c';
      default: return '#95a5a6';
    }
  };

  const getPriorityColor = () => {
    if (!currentTask.due_date) return '#95a5a6';

    const dueDate = new Date(currentTask.due_date);
    const today = new Date();
    const diffTime = dueDate - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays < 0) return '#e74c3c'; // Overdue
    if (diffDays <= 3) return '#f39c12'; // Due soon
    return '#2ecc71'; // On track
  };

  const handleStatusChange = async (newStatus) => {
    try {
      setIsUpdating(true);
      const token = localStorage.getItem('token');
      const response = await axios.put(
        `http://localhost:5000/api/tasks/${currentTask.id}`,
        { status: newStatus },
        {
          headers: {
            'Content-Type': 'application/json',
            'X-User-ID': localStorage.getItem('userId'),
            'Authorization': token ? `Bearer ${token}` : ''
          }
        }
      );

      const updatedTask = response.data;
      setCurrentTask(updatedTask);
      if (onUpdate) onUpdate(updatedTask);
    } catch (err) {
      console.error('Error updating task status:', err);
      setError('Failed to update task status');
    } finally {
      setIsUpdating(false);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        setIsUpdating(true);
        const token = localStorage.getItem('token');
        await axios.delete(
          `http://localhost:5000/api/tasks/${currentTask.id}`,
          {
            headers: {
              'X-User-ID': localStorage.getItem('userId'),
              'Authorization': token ? `Bearer ${token}` : ''
            }
          }
        );

        if (onDelete) onDelete(currentTask.id);
      } catch (err) {
        console.error('Error deleting task:', err);
        setError('Failed to delete task');
        setIsUpdating(false);
      }
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'No due date';
    return format(new Date(dateString), 'MMM d, yyyy');
  };

  if (isEditing) {
    return (
      <div className="task-card editing">
        <TaskForm 
          task={currentTask}
          projects={projects}
          users={users}
          onSuccess={(updatedTask) => {
            setCurrentTask(updatedTask);
            setIsEditing(false);
            if (onUpdate) onUpdate(updatedTask);
          }}
          onCancel={() => setIsEditing(false)}
        />
      </div>
    );
  }

  return (
    <div className="task-card" style={{ opacity: isUpdating ? 0.7 : 1 }}>
      {error && <div className="alert alert-danger">{error}</div>}

      <div className="task-header">
        <div className="task-title-section">
          <h4 className="task-title">{currentTask.title}</h4>
          <div className="task-meta">
            <span 
              className="status-badge"
              style={{ backgroundColor: getStatusColor(currentTask.status) }}
            >
              {currentTask.status.split('_').map(word => 
                word.charAt(0).toUpperCase() + word.slice(1)
              ).join(' ')}
            </span>
            <span 
              className="priority-badge"
              style={{ 
                backgroundColor: getPriorityColor(),
                marginLeft: '8px'
              }}
            >
              {currentTask.priority}
            </span>
          </div>
        </div>

        <div className="task-actions">
          <button 
            className="btn-icon" 
            onClick={() => setIsEditing(true)}
            disabled={isUpdating}
            title="Edit task"
          >
            <i className="fas fa-edit"></i>
          </button>
          <button 
            className="btn-icon danger" 
            onClick={handleDelete}
            disabled={isUpdating}
            title="Delete task"
          >
            <i className="fas fa-trash"></i>
          </button>
        </div>
      </div>

      <p className="task-description">{currentTask.description}</p>

      <div className="task-footer">
        <div className="task-dates">
          <div className="due-date">
            <i className="far fa-calendar-alt"></i>
            <span>{formatDate(currentTask.due_date)}</span>
          </div>
          {currentTask.updated_at && (
            <div className="last-updated">
              <i className="far fa-clock"></i>
              <span>Updated {format(new Date(currentTask.updated_at), 'MMM d, yyyy')}</span>
            </div>
          )}
        </div>

        <div className="task-assignees">
          {assignee ? (
            <div className="assignee" title={`Assigned to ${assignee.name}`}>
              <span className="user-avatar">
                {assignee.name.split(' ').map(n => n[0]).join('')}
              </span>
              <span className="assignee-name">{assignee.name}</span>
            </div>
          ) : (
            <span className="text-muted">Unassigned</span>
          )}
        </div>
      </div>

      <div className="task-status-update">
        <select 
          value={currentTask.status} 
          onChange={(e) => handleStatusChange(e.target.value)}
          className="status-select"
          disabled={isUpdating}
        >
          <option value="pending">Pending</option>
          <option value="in_progress">In Progress</option>
          <option value="completed">Completed</option>
          <option value="blocked">Blocked</option>
        </select>
      </div>
    </div>
  );
};

export default TaskCard;
