import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TaskCard from './TaskCard';
import TaskForm from './TaskForm';

const TaskList = ({ projectId, userId, showProject = true }) => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showTaskForm, setShowTaskForm] = useState(false);
  const [users, setUsers] = useState([]);
  const [projects, setProjects] = useState([]);
  const [filters, setFilters] = useState({
    status: '',
    assignee: '',
    priority: ''
  });

  // Fetch tasks based on filters
  const fetchTasks = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'X-User-ID': localStorage.getItem('userId'),
          'Authorization': token ? `Bearer ${token}` : ''
        },
        params: {}
      };

      // Add filters to params
      if (filters.status) config.params.status = filters.status;
      if (filters.assignee) config.params.assignee_id = filters.assignee;
      if (filters.priority) config.params.priority = filters.priority;
      if (projectId) config.params.project_id = projectId;
      if (userId) config.params.assignee_id = userId;

      const response = await axios.get('http://localhost:5000/api/tasks', config);
      setTasks(response.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
      setError('Failed to load tasks');
    } finally {
      setLoading(false);
    }
  };

  // Fetch users and projects for filters and task creation
  const fetchUsersAndProjects = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'X-User-ID': localStorage.getItem('userId'),
          'Authorization': token ? `Bearer ${token}` : ''
        }
      };

      const [usersRes, projectsRes] = await Promise.all([
        axios.get('http://localhost:5000/api/users', config),
        axios.get('http://localhost:5000/api/projects', config)
      ]);

      setUsers(usersRes.data);
      setProjects(projectsRes.data);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load required data');
    }
  };

  useEffect(() => {
    const loadData = async () => {
      await fetchUsersAndProjects();
      await fetchTasks();
    };
    loadData();
  }, [projectId, userId, filters]);

  const handleCreateTask = async (taskData) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/tasks',
        taskData,
        {
          headers: {
            'Content-Type': 'application/json',
            'X-User-ID': localStorage.getItem('userId'),
            'Authorization': token ? `Bearer ${token}` : ''
          }
        }
      );

      setTasks([response.data, ...tasks]);
      setShowTaskForm(false);
    } catch (err) {
      console.error('Error creating task:', err);
      setError('Failed to create task');
    }
  };

  const handleUpdateTask = (updatedTask) => {
    setTasks(tasks.map(task => 
      task.id === updatedTask.id ? updatedTask : task
    ));
  };

  const handleDeleteTask = (taskId) => {
    setTasks(tasks.filter(task => task.id !== taskId));
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const filteredTasks = tasks.filter(task => {
    if (filters.status && task.status !== filters.status) return false;
    if (filters.assignee && task.assignee_id !== parseInt(filters.assignee)) return false;
    if (filters.priority && task.priority !== filters.priority) return false;
    return true;
  });

  if (loading && tasks.length === 0) {
    return (
      <div className="loading-container">
        <div className="spinner-border" role="status">
          <span className="sr-only">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="task-list-container">
      <div className="task-list-header">
        <h2>{projectId ? 'Project Tasks' : 'My Tasks'}</h2>
        <button 
          className="btn btn-primary"
          onClick={() => setShowTaskForm(!showTaskForm)}
        >
          {showTaskForm ? 'Cancel' : '+ New Task'}
        </button>
      </div>

      {error && <div className="alert alert-danger">{error}</div>}

      {showTaskForm && (
        <div className="task-form-container mb-4">
          <TaskForm 
            projects={projects}
            users={users}
            onSubmit={handleCreateTask}
            onCancel={() => setShowTaskForm(false)}
          />
        </div>
      )}

      <div className="task-filters mb-3">
        <div className="row">
          <div className="col-md-4">
            <div className="form-group">
              <label>Status</label>
              <select 
                name="status" 
                className="form-control"
                value={filters.status}
                onChange={handleFilterChange}
              >
                <option value="">All Statuses</option>
                <option value="pending">Pending</option>
                <option value="in_progress">In Progress</option>
                <option value="completed">Completed</option>
                <option value="blocked">Blocked</option>
              </select>
            </div>
          </div>
          <div className="col-md-4">
            <div className="form-group">
              <label>Assignee</label>
              <select 
                name="assignee" 
                className="form-control"
                value={filters.assignee}
                onChange={handleFilterChange}
              >
                <option value="">All Assignees</option>
                {users.map(user => (
                  <option key={user.id} value={user.id}>
                    {user.name}
                  </option>
                ))}
              </select>
            </div>
          </div>
          <div className="col-md-4">
            <div className="form-group">
              <label>Priority</label>
              <select 
                name="priority" 
                className="form-control"
                value={filters.priority}
                onChange={handleFilterChange}
              >
                <option value="">All Priorities</option>
                <option value="low">Low</option>
                <option value="medium">Medium</option>
                <option value="high">High</option>
                <option value="critical">Critical</option>
              </select>
            </div>
          </div>
        </div>
      </div>

      {filteredTasks.length === 0 ? (
        <div className="no-tasks">
          <p>No tasks found. Create a new task to get started!</p>
        </div>
      ) : (
        <div className="task-grid">
          {filteredTasks.map(task => (
            <TaskCard
              key={task.id}
              task={task}
              users={users}
              projects={projects}
              onUpdate={handleUpdateTask}
              onDelete={handleDeleteTask}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default TaskList;
